using System.Reflection.Metadata; using System.Reflection.PortableExecutable; using var stream = File.OpenRead(args[0]); using var pe = new PEReader(stream); var mr = pe.GetMetadataReader(); int count = 0; foreach (var tdh in mr.TypeDefinitions) { var td = mr.GetTypeDefinition(tdh); var name = mr.GetString(td.Name); var ns = mr.GetString(td.Namespace); if (ns.Contains("MudBlazorCrmApp") && !name.StartsWith("<")) { Console.WriteLine(ns + "." + name + " [" + td.Attributes + "]"); count++; } } Console.WriteLine("Total: " + count);
