using System; using System.Reflection; class P { static void Main(string[] a) { var asm = Assembly.LoadFrom(a[0]); foreach(var t in asm.GetTypes()) if(t.Name=="Main") Console.WriteLine(t.FullName); if(asm.GetTypes().Length==0) Console.WriteLine("NO TYPES"); else { Console.WriteLine("Types count: "+asm.GetTypes().Length); foreach(var t in asm.GetExportedTypes()) Console.WriteLine(t.FullName); } } }
